import React, { useState, useEffect } from 'react';
import './Category.css';
import Header from './Header';

const mockBooks = [
  { id: 1, title: 'Introduction to Algorithms', author: 'K.K. Goyal', category: 'Information Technology', condition: 'Good', price: 1500, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSN9heW9NvrysMLcGkpQKZJ0nWH0f0wjqYYSA&s', rating: 4.8, reviews: 1254 },
  { id: 2, title: 'Introduction to Computer Science', author: 'Pery Donham', category: 'Computer Science', condition: 'Excellent', price: 1200, image: 'https://m.media-amazon.com/images/I/61ZUj9RzWIL._UF1000,1000_QL80_.jpg',  rating: 4.7, reviews: 892 },
  { id: 3, title: 'The Art of Solving Problems', author: 'F. Scott Fitzgerald', category: 'Computer Science', condition: 'Fair', price: 800, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFmWKsLNLPSXVXOgvVqyrt6KfaMW-WUhBbcQ&s', rating: 4.2, reviews: 2341 },
  { id: 4, title: 'Data Science for Beginners', author: 'Harper Lee', category: 'Data Science', condition: 'Good', price: 900, image: 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1562333811l/51080460.jpg', rating: 4.5, reviews: 1876 },
  { id: 5, title: 'Everything An Exclusive', author: 'Phillip Ferraro', category: 'Cyber Security', condition: 'Excellent', price: 700, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLypQ1TvQ_vz0ARjiioMgq03a7OOegffAVGg&s', rating: 4.9, reviews: 3421 },
  { id: 6, title: 'Hacking', author: 'Jon Erickson', category: 'Cyber Security', condition: 'Good', price: 600, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQsM84C-mxj5mlBwDkhF55oKF_L27ZujqbQhg&s', rating: 4.6, reviews: 1987 },
  { id: 7, title: 'Application Software', author: 'George Orwell', category: 'Computer Science', condition: 'Excellent', price: 1700, image: 'data:image/jpeg;base64,',rating: 4.9, reviews: 3421 },
  { id: 8, title: 'Cyber Security Fundamentals', author: 'Rahesh Kumar', category: 'Cyber Security', condition: 'Excellent', price: 700, image: 'data:image/jpeg;base64,', rating: 4.9, reviews: 3421 },
  { id: 9, title: 'Statistic For Data Scientists', author: 'George Orwell', category: 'Data Science', condition: 'Excellent', price: 1800, image: 'data:image/jpeg;base64,', rating: 4.9, reviews: 3421 },
  { id: 10, title: 'C Language', author: 'Dennis M. Ritchie', category: 'Information Technology', condition: 'Excellent', price: 1500, image: 'https://m.media-amazon.com/images/I/71IB4ZMVOtL._UF1000,1000_QL80_.jpg', rating: 4.9, reviews: 3421 },
  { id: 11, title: 'Applied Data Science', author: 'Jake VanderPlas', category: 'Data Science', condition: 'Excellent', price: 750, image: 'https://stdm.github.io/images/apd-cover.jpg',  rating: 4.9, reviews: 3421 },
  { id: 12, title: 'Java Programming', author: 'Joyce Farrell', category: 'Information Technology', condition: 'Excellent', price: 1850, image: 'data:image/jpeg;base64,',rating: 4.9, reviews: 3421 },
];

const Category = ({ viewBookDescription = () => {}, setCurrentPage }) => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [priceRange, setPriceRange] = useState([0, 5000]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [expandedIntros, setExpandedIntros] = useState(new Set());

  useEffect(() => { 
    setBooks(mockBooks); 
  }, []);

  const filteredBooks = books.filter(book => {
    try {
      const matchesSearch = book.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            book.author?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesPrice = book.price >= priceRange[0] && book.price <= priceRange[1];
      const matchesCategory = selectedCategory === 'All' || book.category === selectedCategory;
      return matchesSearch && matchesPrice && matchesCategory;
    } catch (error) {
      console.error('Error filtering books:', error);
      return false;
    }
  });

  const categories = ['All', ...new Set(mockBooks.map(book => book.category))];

  const toggleIntroduction = (bookId) => {
    const newExpanded = new Set(expandedIntros);
    if (newExpanded.has(bookId)) {
      newExpanded.delete(bookId);
    } else {
      newExpanded.add(bookId);
    }
    setExpandedIntros(newExpanded);
  };

  const getConditionColor = (condition) => {
    switch(condition) {
      case 'Excellent': return '#10b981';
      case 'Good': return '#f59e0b';
      case 'Fair': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const renderStars = (rating) => {
    try {
      if (!rating || isNaN(rating)) return <div className="rating-stars"></div>;
      
      const fullStars = Math.floor(rating);
      const hasHalfStar = rating % 1 !== 0;
      const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

      return (
        <div className="rating-stars">
          {''.repeat(fullStars)}
          {hasHalfStar && ''}
          {''.repeat(emptyStars)}
        </div>
      );
    } catch (error) {
      console.error('Error rendering stars:', error);
      return <div className="rating-stars"></div>;
    }
  };

  return (
    <>
      {setCurrentPage && <Header setCurrentPage={setCurrentPage} />}
      <div className="category-container" style={{ marginTop: setCurrentPage ? '80px' : '0' }}>
      <div className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">
            <span className="gradient-text">Book Inventory</span>
          </h1>
          <p className="hero-description">
            Discover, explore, and manage your digital book collection with our modern inventory system
          </p>
          <div className="stats-row">
            <div className="stat-item">
              <span className="stat-number">{filteredBooks.length}</span>
              <span className="stat-label">Books Available</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{categories.length - 1}</span>
              <span className="stat-label">Categories</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">4.6</span>
              <span className="stat-label">Avg Rating</span>
            </div>
          </div>
        </div>
      </div>

      {/* Advanced Filters Section */}
      <div className="filters-section">
        <div className="filters-container">
          <div className="search-container">
            <div className="search-wrapper">
              <svg className="search-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <circle cx="11" cy="11" r="8"></circle>
                <path d="m21 21-4.35-4.35"></path>
              </svg>
              <input 
                type="text" 
                placeholder="Search by title or author..." 
                value={searchTerm} 
                onChange={(e) => setSearchTerm(e.target.value)} 
                className="search-input" 
              />
              {searchTerm && (
                <button className="clear-search" onClick={() => setSearchTerm('')}>
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <line x1="18" y1="6" x2="6" y2="18"></line>
                    <line x1="6" y1="6" x2="18" y2="18"></line>
                  </svg>
                </button>
              )}
            </div>
          </div>

          <div className="filter-group">
            <label className="filter-label">Category</label>
            <select 
              value={selectedCategory} 
              onChange={(e) => setSelectedCategory(e.target.value)} 
              className="category-select"
            >
              {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
            </select>
          </div>

          <div className="filter-group price-filter">
            <label className="filter-label">
              Price Range: <span className="price-values">Rs {priceRange[0].toLocaleString()} - Rs {priceRange[1].toLocaleString()}</span>
            </label>
            <div className="range-container">
              <input 
                type="range" 
                min="0" 
                max="3000" 
                value={priceRange[0]} 
                onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])} 
                className="range-slider"
              />
              <input 
                type="range" 
                min="0" 
                max="3000" 
                value={priceRange[1]} 
                onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])} 
                className="range-slider"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Featured Content Section */}
      <div className="featured-section">
        <div className="featured-content">
          <div className="video-container">
            <video 
              autoPlay 
              muted 
              loop 
              className="featured-video"
              onError={(e) => {
                console.error('Video failed to load:', e);
                e.target.style.display = 'none';
              }}
            >
              <source src="https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4" type="video/mp4" />
              <source src="https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4" type="video/mp4" />
              <source src="data:video/mp4;base64," type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
          <div className="featured-text">
            <h2 className="featured-title">The Power of Books</h2>
            <p className="featured-description">
              Books are gateways to infinite worlds, offering knowledge, wisdom, and inspiration. 
              They shape our thoughts, expand our horizons, and connect us across time and cultures. 
              In our digital age, the timeless value of books remains unchangedthey continue to be 
              essential tools for learning, growth, and human connection.
            </p>
            <div className="featured-highlights">
              <div className="highlight-item">
                <span className="highlight-icon"></span>
                <span>Expand Knowledge</span>
              </div>
              <div className="highlight-item">
                <span className="highlight-icon"></span>
                <span>Inspire Creativity</span>
              </div>
              <div className="highlight-item">
                <span className="highlight-icon"></span>
                <span>Build Empathy</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Books Grid */}
      <div className="books-section">
        <div className="section-header">
          <h2 className="section-title">Available Books</h2>
          <p className="section-subtitle">
            {filteredBooks.length} book{filteredBooks.length !== 1 ? 's' : ''} found
          </p>
        </div>

        <div className="book-grid">
          {filteredBooks.map((book, index) => (
            <div 
              key={book.id} 
              className="book-card"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="book-image-container">
                <img src={book.image} alt={book.title} className="book-image" />
                <div className="condition-badge" style={{ backgroundColor: getConditionColor(book.condition) }}>
                  {book.condition}
                </div>
                <div className="book-overlay">
                  <button 
                    className="quick-view-btn"
                    onClick={() => viewBookDescription(book)}
                  >
                    Quick View
                  </button>
                </div>
              </div>

              <div className="book-content">
                <div className="book-header">
                  <h3 className="book-title">{book.title}</h3>
                  <div className="book-rating">
                    {renderStars(book.rating)}
                    <span className="rating-text">
                      {book.rating || 'N/A'} ({book.reviews || 0})
                    </span>
                  </div>
                </div>

                <p className="book-author">by {book.author}</p>
                <div className="book-meta">
                  <span className="book-category">{book.category}</span>
                  <span className="book-price">
                    Rs {book.price ? book.price.toLocaleString() : 'N/A'}
                  </span>
                </div>

                {book.description ? (
                  <p className="book-description">{book.description}</p>
                ) : (
                  <div className="no-description-section">
                    <button 
                      className="intro-toggle-btn"
                      onClick={() => toggleIntroduction(book.id)}
                    >
                    </button>
                    
                    {expandedIntros.has(book.id) && book.introduction && (
                      <div className="book-introduction">
                        <h4 className="intro-title">Book Introduction</h4>
                        <p className="intro-text">{book.introduction}</p>
                      </div>
                    )}
                  </div>
                )}

                <div className="book-actions">
                  <button 
                    className="primary-btn"
                    onClick={() => viewBookDescription(book)}
                  >
                    View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      </div>
    </>
  );
};

export default Category;
